(11, 1, 'fb_widget', '<iframe src=\"https://www.facebook.com/plugins/page.php?href=https%3A%2F%2Fwww.facebook.com%2Fbolograhak%2F&tabs=timeline&width=500&height=500&small_header=false&adapt_container_width=false&hide_cover=false&show_facepile=false&appId=2692849054189060\" width=\"500\" height=\"500\" style=\"border:none;overflow:hidden\" scrolling=\"no\" frameborder=\"0\" allowfullscreen=\"true\" allow=\"autoplay; clipboard-write; encrypted-media; picture-in-picture; web-share\"></iframe>'),
(12, 1, 'twitter_widget', '<a style=\"height:500px;\" class=\"twitter-timeline\" href=\"https://twitter.com/GrahakBolo?ref_src=twsrc%5Etfw\">Tweets by GrahakBolo</a> <script async src=\"https://platform.twitter.com/widgets.js\" charset=\"utf-8\"></script>'),
(13, 1, 'org_responsibility_content', '<h2 class=\"main-head\"><small>Know more About</small> <br>Organization <span>Responsibility</span></h2>\r\n            <p>Under the Consumer Protection Act 2019 India, an organization has several responsibilities towards consumers. Here are top 9 key points outlining their obligations.</p>\r\n            <div class=\"ul-50 common-ul\">\r\n              <ul>\r\n                <li>Providing accurate information </li>\r\n                <li>Quality assurance</li>\r\n                <li>Ensuring product safety</li>\r\n                <li>Redressal of grievances</li>\r\n                <li>Non-discriminatory practices</li>\r\n                <li>Fair and transparent contracts</li>\r\n<li>Prohibition of unfair trade practices</li>\r\n<li>cooperate with consumer forum</li>\r\n<li>Honoring consumer rights </li>\r\n              </ul>\r\n            </div>'),
